#!/bin/bash

function mfpGet {
	curl http://www.myfitnesspal.com/reports/results/nutrition/$1/300.json -H 'Cookie: known_user=43232220; remember_me=v1%3A43232220%3A9136285532539630a731ca935a15803c; fbsr_186796388009496=_456noBlyrZK7HbHDaQAZXue7n1-Ghy_ud3rRCgtb6g.;' -H 'Referer: http://www.myfitnesspal.com/reports' -o $1.json;
}

for F in Calories Net%20Calories Carbs Fat Protein Fiber Sugar Sodium Potassium; do
	mfpGet $F;
done

mv Net%20Calories.json Net_Calories.json
